mapper.select:
SELECT 

FROM dk_contract a

WHERE a.id = #{id}

SELECT 

FROM dk_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.parent_id = #{parent.id}


		AND a.contract_flag = #{contractFlag}


		AND a.quotation_id = #{dkQuotation.id}


		AND a.contract_number LIKE 
			'%'||#{contractNumber}||'%'
			'%'+#{contractNumber}+'%'
			concat('%',#{contractNumber},'%')


		AND a.member_name LIKE 
			'%'||#{memberName}||'%'
			'%'+#{memberName}+'%'
			concat('%',#{memberName},'%')


		AND a.mobile LIKE 
			'%'||#{mobile}||'%'
			'%'+#{mobile}+'%'
			concat('%',#{mobile},'%')


		AND a.install_by = #{installUser.id}


		AND a.sale_by = #{saleUser.id}


		AND a.product_type = #{productType}


		AND a.review_status = #{reviewStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dk_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM dk_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM dk_quotation a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
and a.review_status = #{reviewStatus} and not exists(select 1 from dk_contract dc where dc.quotation_id = a.id)
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM dk_member a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	*
FROM dk_contract a
WHERE 1 = 1

	AND a.name = #{name}


	AND a.contract_number = #{contractNumber} 


	AND a.id != #{id}


	AND a.contract_flag = #{contractFlag}

mapper.update:
UPDATE dk_contract SET review_status = #{reviewStatus},
is_review = #{isReview}, review_by = #{updateBy.id}, review_time = #{updateDate},
update_by = #{updateBy.id}, update_date = #{updateDate} WHERE id = #{id}

UPDATE dk_contract SET 
	arrive_fee = arrive_fee + #{arriveFee}
WHERE id = #{id}
