mapper.select:
SELECT 

FROM dk_contract_install a

WHERE a.id = #{id}

SELECT 

FROM dk_contract_install a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.contract_id = #{dkContract.id}


		AND a.pay_date BETWEEN #{beginPayDate} AND #{endPayDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dk_contract_install a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_contract_install SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM dk_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
