mapper.select:
SELECT 

FROM dk_contract_pay a

WHERE a.id = #{id}

SELECT 

FROM dk_contract_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dk_contract_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_contract_pay SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
