mapper.select:
SELECT 
	
FROM dk_contract_product a

WHERE a.id = #{id}

SELECT 
	
FROM dk_contract_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.contract_id = #{contractId}
	
	
		AND a.contract_id = #{dkContract.id}
	
	
		AND a.product_id = #{productId}
	
	
	${sqlMap.dsf}


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.create_date ASC

SELECT 
	
FROM dk_contract_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM dk_contract_product

	
		WHERE id = #{id}
	
	
		WHERE contract_id = #{contractId}

UPDATE dk_contract_product SET 
	del_flag = #{DEL_FLAG_DELETE}

	
		WHERE id = #{id}
	
	
		WHERE contract_id = #{contractId}

mapper.select:
SELECT * FROM dk_product a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM dk_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
