mapper.select:
SELECT 

FROM dk_contract_schedule a

WHERE a.id = #{id}

SELECT 

FROM dk_contract_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.contract_id = #{contractId}


		AND a.submit_date BETWEEN #{beginSubmitDate} AND #{endSubmitDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dk_contract_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_contract_schedule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM dk_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
