mapper.select:
SELECT 

FROM dk_contract_service a

WHERE a.id = #{id}

SELECT 

FROM dk_contract_service a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.contract_id = #{dkContract.id}


		AND a.service_date BETWEEN #{beginServiceDate} AND DATE_ADD(#{endServiceDate}, INTERVAL 1 DAY)


		AND a.service_person LIKE 
			'%'||#{servicePerson}||'%'
			'%'+#{servicePerson}+'%'
			concat('%',#{servicePerson},'%')


		AND a.review_status = #{reviewStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dk_contract_service a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_contract_service SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM dk_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_contract_service SET review_status = #{reviewStatus},
is_review = #{isReview}, review_by = #{updateBy.id},
update_by = #{updateBy.id}, update_date = #{updateDate} WHERE id = #{id}
