mapper.select:
SELECT 

FROM dk_member a

WHERE a.id = #{id}

SELECT
FROM dk_member a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.mobile = #{mobile}
AND a.nick_name LIKE '%'||#{nickName}||'%' '%'+#{nickName}+'%' concat('%',#{nickName},'%')
AND a.sex LIKE '%'||#{sex}||'%' '%'+#{sex}+'%' concat('%',#{sex},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM dk_member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
