mapper.select:
SELECT 

FROM dk_product a

WHERE a.id = #{id}

SELECT 

FROM dk_product a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.model = #{model}


		AND a.classify_id = #{classifyId}


		AND a.product_type = #{productType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dk_product a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dk_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
